import 'package:equatable/equatable.dart';import 'package:meta/meta.dart';import 'package:sunshine/models/weather.dart';import 'package:sunshine/models/weather_forecast.dart';@immutableabstract class WeatherState extends Equatable {}class InitialWeatherState extends WeatherState {  @override  List<Object> get props => [];}class WeatherLoadingState extends WeatherState {  @override  List<Object> get props => [];}class WeatherLoadedState extends WeatherState {  final WeatherModel weather;  WeatherLoadedState(this.weather);  @override  List<Object> get props => [weather];}class WeatherForecastLoadedState extends WeatherState {  final WeatherForecastModel weather;  WeatherForecastLoadedState(this.weather);  @override  List<Object> get props => [weather];}class WeatherLoadingErrorState extends WeatherState {  final error;  WeatherLoadingErrorState({this.error});  @override  List<Object> get props => [error];}