import 'package:http/http.dart' as http;import 'package:sunshine/config/config.dart';import 'package:sunshine/models/massage.dart';import 'package:sunshine/models/weather.dart';import 'package:sunshine/models/weather_forecast.dart';// Weather repositoryclass WeatherRepository {  // getting current city weather  Future<WeatherModel> getWeather(String city) async {    final result = await http.Client().get('$BASE_URL/weather?q=$city&APPID=$APP_ID&units=metric');    if (result.statusCode != 200) throw Exception(messageFromJson(result.body));    return weatherModelFromJson(result.body);  }  // getting weather forecast (not used)  Future<WeatherForecastModel> getWeatherForecast(String city) async {    final result = await http.Client().get('$BASE_URL/forecast?q=$city&APPID=$APP_ID&units=metric');    if (result.statusCode != 200) throw Exception(messageFromJson(result.body));    print(result.body);    return weatherForecastModelFromJson(result.body);  }}